import React from 'react'
import { useStateValue } from "./StateProvider";
import checkoutad from "./assets/checkout-ad.jpg";
import "./styles/Checkout.css";

export default function Checkout() {
    const [{ basket }, _] = useStateValue();
    return (
        <div className="checkout">
            <div class="checkout-ad">
                <p><b>Pay faster for all your shopping needs with <red>Amazon Pay balance</red></b></p>
                <p>Get Instant refund on cancellations | Zero payment failures</p>
            </div>
            {basket?.length === 0 ? (
                <div>Your Shopping Basket is Empty</div>
            ) : (
                    <div>Your Shopping Basket</div>
                )
            }
        </div>
    )
}
