export const initialState = {
    basket: [{
        id: "12345",
        title: "Headset"
    }],
};

function reducer(state, action) {
    console.log(action);
    switch (action.type) {
        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: [...state.basket, action.item]
            }
        case 'REMOVE_FROM_BASKET':
            break;
        default:
            return state;
    }
}

export default reducer;
